mapper.select:
select g.id,
       g.codeshare,
       g.charge,
       g.monitor,
       g.class_name AS className,
       g.commissary,
       g.create_time AS createTime
from grade g


       g.codeshare=#{vo.codeshare}


      and g.charge=#{vo.charge}


     and  g.monitor=#{vo.monitor}


     and  g.class_name=#{vo.className}


     and  g.commissary=#{vo.commissary}


order by g.create_time DESC
